import { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function GameListAndPartyIndex() {
  const [date, setDate] = useState(null);
  const [games, setGames] = useState([]);

  const fetchGames = async () => {
    if (!date) return;
    const isoDate = date.toISOString().slice(0, 10); // YYYY-MM-DD
    const res = await fetch(`/games/${isoDate}`);
    const data = await res.json();
    setGames(data);
  };

  return (
    <div>
      <h2>Party Index by Date</h2>
      <DatePicker
        selected={date}
        onChange={setDate}
        dateFormat="yyyy-MM-dd"
        placeholderText="Select a date"
        maxDate={new Date()}
        isClearable
      />
      <button onClick={fetchGames} disabled={!date}>Get Games</button>
      <ul>
        {games.map(game => (
          <li key={game.game_id}>
            <b>{game.away_team}</b> @ <b>{game.home_team}</b> â€” 
            Score: {game.away_points} - {game.home_points} | 
            Party Index: <b>{game.party_index}</b>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GameListAndPartyIndex;
