import { use, useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function GameListAndPartyIndex() {
  const [date, setDate] = useState(new Date("2024-04-18"));
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchGames = async () => {
    if (!date) return;
    setLoading(true);
    setError("")
    setGames([]);
    try{
      const isoDate = date.toISOString().slice(0, 10); // YYYY-MM-DD
      const res = await fetch(`/get-games-from-date/${isoDate}`);
      if (!res.ok) throw new Error("Failed to fetch games")
      const data = await res.json();
      setGames(data);
    } catch (err) {
      setError("Could not fetch games for that date.")
    } finally {
      setLoading(false);
    }


  };

  return (
    <div>
      <h1 className="page-header">NHL Party Index</h1>
      <h3 className="page-subheader">
        An analytical approach to identifying the statistical impact of road games that have a higher potential for off-ice festitives
      </h3>
      <div className="datepicker-wrapper">
        <DatePicker
          selected={date}
          onChange={setDate}
          dateFormat="yyyy-MM-dd"
          placeholderText="Select a date"
          maxDate={new Date()}
          showMonthDropdown
          showYearDropdown
          dropdownMode="select"
          isClearable
          renderCustomHeader={({
            date,
            changeYear,
            changeMonth,
            decreaseMonth,
            increaseMonth,
            prevMonthButtonDisabled,
            nextMonthButtonDisabled
          }) => (
            <div className="react-datepicker__header-row">
              <button
                onClick={decreaseMonth}
                disabled={prevMonthButtonDisabled}
                className="react-datepicker__navigation react-datepicker__navigation--previous"
                aria-label="Previous Month"
                type="button"
              >
                {"<"}
              </button>
              {/* Month dropdown */}
              <select
                value={date.getMonth()}
                onChange={e => changeMonth(Number(e.target.value))}
                className="react-datepicker__month-dropdown-container"
              >
                {Array.from({ length: 12 }, (_, i) => (
                  <option key={i} value={i}>
                    {new Date(0, i).toLocaleString("default", { month: "long" })}
                  </option>
                ))}
              </select>
              {/* Year dropdown */}
              <select
                value={date.getFullYear()}
                onChange={e => changeYear(Number(e.target.value))}
                className="react-datepicker__year-dropdown-container"
              >
                {Array.from({ length: 10 }, (_, i) => {
                  const year = new Date().getFullYear() - 5 + i;
                  return (
                    <option key={year} value={year}>
                      {year}
                    </option>
                  );
                })}
              </select>
              <button
                onClick={increaseMonth}
                disabled={nextMonthButtonDisabled}
                className="react-datepicker__navigation react-datepicker__navigation--next"
                aria-label="Next Month"
                type="button"
              >
                {">"}
              </button>
            </div>
          )}
        />
      <button className = "get-games-button" onClick={fetchGames} disabled={!date || loading}>
        {loading? "Loading..." : "Get Games"}
      </button>
      </div>
      <ul>
        {games.map(game => (
          <li  className="games-list" key={game.id}>
            <b>{game.awayTeam_abbrev} {game.awayTeam_score}</b> @ <b>{game.homeTeam_abbrev} {game.homeTeam_score}</b>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GameListAndPartyIndex;
