import { use, useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

function GameListAndPartyIndex() {
  const [date, setDate] = useState(new Date("2024-04-18"));
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchGames = async () => {
    if (!date) return;
    setLoading(true);
    setError("")
    setGames([]);
    try{
      const isoDate = date.toISOString().slice(0, 10); // YYYY-MM-DD
      const res = await fetch(`/get-games-from-date/${isoDate}`);
      if (!res.ok) throw new Error("Failed to fetch games")
      const data = await res.json();
      setGames(data);
    } catch (err) {
      setError("Could not fetch games for that date.")
    } finally {
      setLoading(false);
    }


  };

  return (
    <div>
      <h1 className="page-header">NHL Party Index</h1>
      <h3 className="page-subheader">
        An analytical approach to identifying the statistical impact of road games that have a higher potential for off-ice festitives
      </h3>
      <div className="datepicker-wrapper">
        <DatePicker
          selected={date}
          onChange={setDate}
          dateFormat="yyyy-MM-dd"
          placeholderText="Select a date"
          maxDate={new Date()}
          showMonthDropdown
          showYearDropdown
          dropdownMode="select"
          isClearable
        />
      <button className = "get-games-button" onClick={fetchGames} disabled={!date || loading}>
        {loading? "Loading..." : "Get Games"}
      </button>
      </div>
      <ul>
        {games.map(game => (
          <li  className="games-list" key={game.id}>
            <b>{game.awayTeam_abbrev} {game.awayTeam_score}</b> @ <b>{game.homeTeam_abbrev} {game.homeTeam_score}</b>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default GameListAndPartyIndex;
